import React, {useEffect, useReducer} from 'react';
//import {getAll} from '../../services/foodService.js';
import Search from '../../components/Search/Search.js';
import Tags from '../../components/Tags/Tags.js';
import { getAll, getAllTags, search } from '../../services/foodService.js';

import Thumbnails from '../../components/Thumbnails/Thumbnails.js';
import { useParams } from 'react-router-dom';

const initialState ={ foods : [] };

const reducer = (state, action)=>{
  switch (action.type) {
    case 'FOOD_LOADED':
      return { ...state, foods: action.payload };
    case 'TAGS_LOADED':
      return { ...state, tags: action.payload };
    default:
      return state;
  }
};
export default function HomePage() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { foods }= state;
  const {searchTerm}= useParams();

  useEffect (()=> {
    getAllTags().then(tags => dispatch({type: 'TAGS_LOADED', payload : tags }));
    const loadFoods = searchTerm ? search(searchTerm) : getAll();
    loadFoods.then(foods=> dispatch({type: 'FOOD_LOADED', payload: foods}));
}, [searchTerm]);
  return( <>
  <Search />
  <Tags />
  <Thumbnails foods={foods}/>
  </>
  );
}
