import React from 'react';
import { useCart } from '../../hooks/useCart';
import classes from './cartPage.module.css';
import Title from '../../components/Title/Title';
import { Link } from 'react-router-dom';
import Price from '../../components/Price/Price';
import NotFound from '../../components/NotFound/NotFound';

export default function CartPage() {
  const { cart, removeFromCart, changeQuantity } = useCart();
  return( <> 
    <Title title="Cart page" margin ="1.5rem 0 0 2.5rem"/>
    {cart.items.length ===0 ? (
      <NotFound messege="Cart Page Is Empty!" />
     ) : (
    <div className={classes.container}>
      <ul className={classes.list}>
        {cart.items.map(item=> (
        <li key={item.food.id}>
          <div>
            <img src={`/foods/${item.food.imageUrl}`}
             alt={item.food.name}
             />
          </div>
          <div>
            <Link to={`/food/${item.food.id}`}>{item.food.name}</Link>
          </div>
          <div>
            <select value={item.quantity} onChange={e => changeQuantity(item, Number(e.target.value))}>
              <option>1</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option>5</option>
              <option>6</option>
              <option>7</option>
              <option>8</option>
              <option>9</option>
              <option>10</option>
            </select>
          </div>
          <div>
            <Price price={item.price}/>
          </div>
          <div>
            <button className={classes.remove_button}
            onClick={() => removeFromCart(item.food.id)}>
              Remove
            </button>
          </div>
        </li>
        ))}
        </ul>
        <div className={classes.checkout}>
        <div>
        <div className={classes.foods_count}>{cart.totalCount}</div>
        <div className={classes.total_price}>
          <Price price={cart.totalPrice}/>
        </div>
        </div>
        <Link to="/checkout">Proceed To Checkout</Link>
        </div>
        </div>
    )}  
  </>
   );
}
